class SurveyForm extends Component{
  render(){
    return(
      <div>
        <form onSubmit={this.props.handleSubmit(values=>console.log(values))}>
          <Field type="text" name="surveyTitle" component="input" />
          <button type="submit">submit</button>
        </form>
     </div>
   );
  }
}
